import React from "react";
import { styled } from '@mui/material/styles';

const Login_BG = styled('div')({
  backgroundImage: `url($(src/assets/images/login_bg.png))`,
  backgroundPosition: `center`,
  backgroundSize: `cover`,
  backgroundRepeat: `no-repeat`,
  border: `1px solid rgba(0, 0, 0, 1)`,
  boxSizing: `border-box`,
  borderRadius: `0px`,
  display: `flex`,
  position: `relative`,
  isolation: `isolate`,
  flexDirection: `row`,
  width: '100%',
  height: `1024px`,
  justifyContent: `flex-start`,
  alignItems: `flex-start`,
  padding: `0px`,
  overflow: `hidden`,
});


const Login = () => {
  return (
    <Container>
      <BackgroundImage loading="lazy" alt="login_bg" />
      <Content>
        <Logo loading="lazy" srcSet="https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=100 100w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=200 200w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=400 400w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=800 800w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=1200 1200w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=1600 1600w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=2000 2000w, https://cdn.builder.io/api/v1/image/assets/TEMP/07202251-231e-440b-8cfa-4ff8e1956985?apiKey=5eee8e5597a74884b2ae231bd09a91a5&"/>
        <Text>
          <span style={{ color: "rgba(0,0,0,1)" }}>Bạn chưa có tài khoản ?</span>
          <span style={{ color: "rgba(27,106,170,1)" }}> Đăng kí ngay</span>
          <span style={{ color: "rgba(0,0,0,1)" }}></span>
        </Text>
        <GoogleLogin>
          <GoogleLogo loading="lazy" srcSet="https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=100 100w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=200 200w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=400 400w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=800 800w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=1200 1200w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=1600 1600w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&width=2000 2000w, https://cdn.builder.io/api/v1/image/assets/TEMP/5736e9f7-956b-4be8-94be-71e2d46dcc78?apiKey=5eee8e5597a74884b2ae231bd09a91a5&"/>
          <GoogleText>Đăng nhập bằng Google</GoogleText>
        </GoogleLogin>
        <OrSection>
          <Line />
          <OrText>
            <Line />
            <OrTextContent>Hoặc</OrTextContent>
            <Line />
          </OrText>
          <Line />
        </OrSection>
        <InputSection>
          <InputLabel>Email</InputLabel>
          <InputValue>admin@hcmut.edu.vn</InputValue>
          <InputLabel>Mật khẩu</InputLabel>
          <InputValue>************</InputValue>
        </InputSection>
        <Button>Đăng nhập</Button>
        <ForgotPassword>Quên mật khẩu?</ForgotPassword>
      </Content>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 1px solid #000;
  overflow: hidden;
  position: relative;
  display: flex;
  padding: 0 20px;
`;

const BackgroundImage = styled.img`
  position: absolute;
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: center;
`;

const Content = styled.div`
  position: relative;
  border-radius: 20px;
  background-color: #fff;
  display: flex;
  width: 528px;
  max-width: 100%;
  flex-direction: column;
  align-items: center;
  margin: 87px 0 228px;
  padding: 25px 38px 25px 39px;
  @media (max-width: 991px) {
    margin: 40px 0;
    padding: 0 20px;
  }
`;

const Logo = styled.img`
  aspect-ratio: 1.01;
  object-fit: contain;
  object-position: center;
  width: 80px;
  overflow: hidden;
  max-width: 100%;
`;

const Text = styled.div`
  color: #1b6aaa;
  margin-top: 22px;
  white-space: nowrap;
  font: 400 18px/27px Poppins, sans-serif;
  @media (max-width: 991px) {
    white-space: initial;
  }
`;

const GoogleLogin = styled.div`
  display: flex;
  margin-top: 19px;
  width: 278px;
  max-width: 100%;
  gap: 6px;
`;

const GoogleLogo = styled.img`
  aspect-ratio: 0.9;
  object-fit: contain;
  object-position: center;
  width: 54px;
  overflow: hidden;
  max-width: 100%;
`;

const GoogleText = styled.div`
  color: #000;
  align-self: center;
  white-space: nowrap;
  margin: auto 0;
  font: 400 18px/27px Poppins, sans-serif;
  @media (max-width: 991px) {
    white-space: initial;
  }
`;

const OrSection = styled.div`
  align-self: stretch;
  display: flex;
  margin-top: 22px;
  justify-content: space-between;
  gap: 0px;
  @media (max-width: 991px) {
    max-width: 100%;
    flex-wrap: wrap;
  }
`;

const Line = styled.div`
  border-bottom: 1px solid #000;
  display: flex;
  width: 198px;
  height: 22px;
  flex-direction: column;
`;

const OrText = styled.div`
  display: flex;
  flex-grow: 1;
  flex-basis: 0%;
  flex-direction: column;
  padding: 2px 0;
`;

const OrTextContent = styled.div`
  color: #000;
  white-space: nowrap;
  margin: -20px 0 4px;
